package JavaSequencial;

import java.util.Scanner;

public class Sequencial6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double x1,x2,y1,y2,d;
		
		Scanner ler=new Scanner (System.in);
		
		System.out.printf("Digite o valor de x1: ");
		x1=ler.nextFloat();
		System.out.printf("\nDigite o valor de x2: ");
		x2=ler.nextFloat();
		System.out.printf("\nDigite o valor de y1: ");
		y1=ler.nextFloat();
		System.out.printf("\nDigite o valor de y2: ");
		y2=ler.nextFloat();
		
		d=Math.sqrt(Math.pow((x2-x1),2)+Math.pow((y2-y1),2));
		
		System.out.printf("\nA dist√¢ncia entre os dois pontos foi de: %.2f",d);
	}

}
